% load(strcat(directoryVid,'trajectories_2.mat'));

    Lpiv=dir(strcat('E:\Clément\Nappe\essaispiv2\','*.tif'));
    Lvid=Lpiv;
    directoryPiv=strcat('E:\Clément\Nappe\essaispiv2\');
    global parload_pathname;
    estimator=1;
   index_header=6;
   
   %[out,idx] = sort(ns);
    
    
    index = 'tif';
    
    
    sav_index='mat';
    pathname_sav=directoryPiv;
    filename_sav='result';
    
    for field=1:length(Lpiv)/2
        image_filename_1(field)={strcat(directoryPiv,Lpiv(2*field-1).name)};
        image_filename_2(field)={strcat(directoryPiv,Lpiv(2*field).name)};
        if length(image_filename_1{field})==36
            namesav=image_filename_1{field}(end-7:end-6)
        else
            namesav=image_filename_1{field}(end-6)
        end
        sav_filename(field)={strcat(pathname_sav,namesav,'.',sav_index)};
    end
    pos = get(gcf,'Position');close;pause(.1)
    Automaticprocess_3;
    

